import { Suspense, useState, useEffect } from 'react';
import { useLoaderData, json, defer, Await, redirect } from 'react-router-dom';

import RocketsList from '../components/RocketsList';
import Pagination from '../components/Pagination/Pagination';

function RocketsPage(props) {
  const { rockets, page, perPage } = useLoaderData('rocket-get-all');
  const [currentPage, setCurrentPage] = useState(page);
console.log('linha 10',props.rockets);
  const totalPage = Math.ceil(rockets.length / perPage);

  function onClickPagination(index) {
    setCurrentPage(index + 1)
  }

  useEffect(async () => {
console.log('linha 11',rockets.getData());

    // redirect(`?page=${page}&perPage=${perPage}`)
  }, [currentPage]);


  return (
    <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
      <Await resolve={rockets.data}>
        {(loadedEvents) => {
          <div>
            <RocketsList rockets={loadedEvents} edit={false} />
            {/* <Pagination
              currentPage={currentPage}
              totalPage={totalPage}
              onClick={onClickPagination}
            /> */}
          </div>;
        }
        }
      </Await>
    </Suspense>
  );
}

export default RocketsPage;

async function loadRockets(page, perPage) {
  // const response = await fetch('http://localhost:8080/events?page=' + page + '&perPage=' + perPage);
  const response = await fetch('http://localhost:8080/events?page=1&perPage=10');
  if (!response.ok) {
    throw json(
      { message: 'Could not fetch events.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    console.log('linha 56',resData.events);
    return [
     { date: "2022-10-01",description: "Some awesome event!",
     id: "2a42fcc4-ea21-4bdd-abf6-c40006dc66a4",
     image: "https://cdn.muenchen-p.de/fl_progressive,q_65/.imaging/stk/responsive/teaser300/dms/va-2016/muenchner-christkindlmarkt/christkindlmarkt-marienplat-logo-hp/document/christkindlmarkt-marienplat-logo-hp.jpg"
     ,title: "01"}
    ];
  }
}

export function loader({ request }) {
  const searchParams = new URL(request.url).searchParams;
  const page = searchParams.get('page') || 1;
  const perPage = searchParams.get('perPage') || 20;
  return defer({
    rockets: loadRockets(page, perPage),
    page,
    perPage,
  });
}
