import { Suspense } from 'react';
import { useLoaderData, json, defer, Await } from 'react-router-dom';

import RocketsList from '../components/RocketsList';

function RocketsPage() {
  const { rockets, page, perPage } = useLoaderData();
  const [currentPage, setCurrentPage] = useState(page);

  const totalPage = Math.ceil(rockets.length / perPage);

  function onClickPagination(index) {
    setCurrentPage(index + 1)
  }

  useEffect(() => {
    fetchRockets();
  }, [currentPage]);

  return (
    <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
      <Await resolve={rockets}>
        {(loadedEvents) => {
          <div>
            <RocketsList rockets={loadedEvents} edit={false} />
            <Pagination
              currentPage={currentPage}
              totalPage={totalPage}
              onClick={onClickPagination}
            />
          </div>;
        }
        }
      </Await>
    </Suspense>
  );
}

export default RocketsPage;

async function loadRockets() {
  const response = await fetch('http://localhost:8080/events');

  if (!response.ok) {
    throw json(
      { message: 'Could not fetch events.' },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json();
    return resData.events;
  }
}

export function loader({ request, params }) {
  const searchParams = new URL(request.url).searchParams;
  const page = searchParams.get('page') || 1;
  const perPage = searchParams.get('perPage') || 2;
  return defer({
    rockets: loadRockets(),
    page,
    perPage,
  });
}
